import React, { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import CountdownSection from './CountdownSection';
import './App.css';

// Componente para animaciones de aparición
const AnimatedSection = ({ children, animation = 'fadeUp', delay = 0, className = '', ...props }) => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <div
      ref={ref}
      className={`animated-element ${animation} ${inView ? 'animate-in' : ''} ${className}`}
      style={{ animationDelay: `${delay}ms` }}
      {...props}
    >
      {children}
    </div>
  );
};

function App() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isInCountdownSection, setIsInCountdownSection] = useState(true);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setMobileMenuOpen(false);
  };

  // Detect if we're in countdown section
  React.useEffect(() => {
    const handleScroll = () => {
      const countdownSection = document.getElementById('countdown');
      if (countdownSection) {
        const rect = countdownSection.getBoundingClientRect();
        const isVisible = rect.top <= 100 && rect.bottom >= 100;
        setIsInCountdownSection(isVisible);
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Check initial position

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
