            <span className={`hamburger-line ${mobileMenuOpen ? 'line-1-active' : ''} ${isInCountdownSection ? 'hamburger-line-white' : ''}`}></span>
            <span className={`hamburger-line ${mobileMenuOpen ? 'line-2-active' : ''} ${isInCountdownSection ? 'hamburger-line-white' : ''}`}></span>
            <span className={`hamburger-line ${mobileMenuOpen ? 'line-3-active' : ''} ${isInCountdownSection ? 'hamburger-line-white' : ''}`}></span>
          </div>
        </div>
      </nav>

      {/* PÁGINA PRINCIPAL */}
