import React, { useState, useEffect, useRef } from "react"

// Updated with actual event images
const images = [
  "/assets/images/064.webp",
  "/assets/images/070.webp", 
  "/assets/images/090.webp",
  "/assets/images/092.webp",
  "/assets/images/132.webp",
  "/assets/images/172.webp",
]

const IMAGE_CHANGE_INTERVAL = 3000 // Increased to 3 seconds for better UX

// Menu icon components (simple SVG)
const Menu = ({ size = 24 }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="3" y1="6" x2="21" y2="6"/>
    <line x1="3" y1="12" x2="21" y2="12"/>
    <line x1="3" y1="18" x2="21" y2="18"/>
  </svg>
)

const X = ({ size = 24 }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="18" y1="6" x2="6" y2="18"/>
    <line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
)

export default function CountdownSection() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0)
  const [imagesLoaded, setImagesLoaded] = useState(false)
  const [loadedImages, setLoadedImages] = useState(new Set())
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [isClient, setIsClient] = useState(false)
  const sectionRef = useRef(null)

  // Navigation items
  const navItems = [
    { name: "GRADUACIÓN", href: "#inicio" },
    { name: "SAVE THE DATE", href: "#save-date" },
    { name: "MISA", href: "#misa" },
    { name: "PROM", href: "#prom" },
    { name: "VESTIMENTA", href: "#dress-code" }
  ]

  // Smooth scroll function
  const scrollToSection = (href) => {
    const element = document.querySelector(href)
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' })
      setMobileMenuOpen(false) // Close mobile menu after navigation
    }
  }

  // Preload all images
  useEffect(() => {
    const preloadImages = () => {
      const imagePromises = images.map((src, index) => {
        return new Promise((resolve) => {
          const img = new Image()
          img.onload = () => resolve(index)
          img.onerror = () => resolve(index) // Still resolve to avoid hanging
          img.src = src
        })
      })

      Promise.all(imagePromises).then((loadedIndexes) => {
        setLoadedImages(new Set(loadedIndexes))
        setImagesLoaded(true)
      })
    }

    preloadImages()
  }, [])

  // Image slideshow effect - only start after images are loaded
  useEffect(() => {
    if (!imagesLoaded) return

    const imageInterval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length)
    }, IMAGE_CHANGE_INTERVAL)

    return () => clearInterval(imageInterval)
  }, [imagesLoaded])

  // Set isClient to true after component mounts
  useEffect(() => {
    setIsClient(true)
  }, [])

  // Calculate time to event
  const calculateTimeLeft = () => {
    const eventDate = new Date('2025-06-29T19:00:00')
    const currentTime = new Date()
    const difference = eventDate.getTime() - currentTime.getTime()

    if (difference > 0) {
      return {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60)
      }
    }

    return { days: 0, hours: 0, minutes: 0, seconds: 0 }
  }

  const [countdown, setCountdown] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 })

  useEffect(() => {
    if (!isClient) return

    // Initialize countdown immediately after client mounts
    setCountdown(calculateTimeLeft())

    const countdownTimer = setInterval(() => {
      setCountdown(calculateTimeLeft())
    }, 1000)
    return () => clearInterval(countdownTimer)
  }, [isClient])

  return (
    <div id="countdown" ref={sectionRef} className="countdown-section">
      {/* Background Image Slideshow with improved loading */}
      <div
        className={`countdown-bg-slideshow ${
          !imagesLoaded ? 'countdown-bg-loading' : 'countdown-bg-loaded'
        }`}
        style={{
          backgroundImage: `url(${images[currentImageIndex]})`,
        }}
      />

      {/* Loading state overlay */}
      {!imagesLoaded && (
        <div className="countdown-loading-overlay">
          <div className="countdown-loading-content">
            <div className="countdown-spinner"></div>
            <p className="countdown-loading-text">Cargando...</p>
          </div>
        </div>
      )}

      {/* Overlay */}
      <div className="countdown-overlay">
        {/* Top Navigation */}
        <nav className="countdown-nav">
          <span className="countdown-brand">@EUFORIA</span>
          
          {/* Desktop Navigation */}
          <div className="countdown-nav-desktop">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => scrollToSection(item.href)}
                className="countdown-nav-link"
              >
                {item.name}
              </button>
            ))}
          </div>

          {/* Mobile Menu Button */}
          <button
            className="countdown-mobile-toggle"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            {mobileMenuOpen ? <X size={22} /> : <Menu size={22} />}
          </button>
        </nav>

        {/* Mobile Menu Full Screen */}
        <div 
          className={`countdown-mobile-menu ${
            mobileMenuOpen ? 'countdown-mobile-menu-open' : 'countdown-mobile-menu-closed'
          }`}
        >
          <div className="countdown-mobile-menu-content">
            {navItems.map((item, index) => (
              <button
                key={item.name}
                onClick={() => scrollToSection(item.href)}
                className="countdown-mobile-nav-link"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                {item.name}
              </button>
            ))}
          </div>
        </div>

        {/* Main Content */}
        <main className="countdown-main">
          {/* Event Branding */}
          <div className="countdown-branding">
            <span className="countdown-subtitle">
              ® EUFORIA PRESENTA
            </span>

            {/* Title with Drop Cap */}
            <div className="countdown-title">
              <span className="countdown-drop-cap">
                P
              </span>
              ROM LEM 129
            </div>
          </div>

          {/* Countdown Container */}
          <div className="countdown-timer">
            <div className="countdown-unit">
              <span className="countdown-number">
                {isClient ? String(countdown.days).padStart(2, "0") : "00"}
              </span>
              <span className="countdown-label">DÍAS</span>
            </div>

            <div className="countdown-colon">:</div>

            <div className="countdown-unit">
              <span className="countdown-number">
                {isClient ? String(countdown.hours).padStart(2, "0") : "00"}
              </span>
              <span className="countdown-label">HRS</span>
            </div>

            <div className="countdown-colon">:</div>

            <div className="countdown-unit">
              <span className="countdown-number">
                {isClient ? String(countdown.minutes).padStart(2, "0") : "00"}
              </span>
              <span className="countdown-label">MIN</span>
            </div>

            <div className="countdown-colon">:</div>

            <div className="countdown-unit">
              <span className="countdown-number">
                {isClient ? String(countdown.seconds).padStart(2, "0") : "00"}
              </span>
              <span className="countdown-label">SEG</span>
            </div>
          </div>
        </main>

        {/* Bottom Navigation */}
        <footer className="countdown-footer">{/* Footer content if needed */}</footer>
      </div>
    </div>
  )
}
